import React, { useState, useEffect } from 'react';
import './Home.css';
import { Link } from 'react-router-dom';

function Home() {
  const calculateTimeLeft = () => {
    const weddingDate = new Date("2026-08-15T12:00:00"); // Set your wedding date here
    const now = new Date();
    const difference = weddingDate - now;

    let timeLeft = {};

    if (difference > 0) {
      timeLeft = {
        days: Math.floor(difference / (1000 * 60 * 60 * 24)),
        hours: Math.floor((difference / (1000 * 60 * 60)) % 24),
      };
    }

    return timeLeft;
  };

  const [timeLeft, setTimeLeft] = useState(calculateTimeLeft());

  useEffect(() => {
    const timer = setTimeout(() => {
      setTimeLeft(calculateTimeLeft());
    }, 1000);

    return () => clearTimeout(timer);
  });

  const timerComponents = [];

  Object.keys(timeLeft).forEach((interval) => {
    if (!timeLeft[interval]) {
      return;
    }

    timerComponents.push(
      <span key={interval}>
        {timeLeft[interval]} {interval}{" "}
      </span>
    );
  });

  return (
    <div className="home-container">
      {/* Navigation Menu */}
      <nav className="navbar">
        <ul className="nav-list">
          <li><Link to="/">Home</Link></li>
          <li><Link to="/rsvp">RSVP</Link></li>
          <li><Link to="/hitta-hit">Hitta hit</Link></li>
        </ul>
      </nav>

      <header className="hero-section">
        <div className="hero-overlay">
          <h1>Välkommen till Arthur & Amandas bröllop!</h1>
          <p>Fira med oss på DEN BÄSTA DAGEN!</p>
        </div>
      </header>

      {/* Countdown Timer Section */}
      <section className="countdown-section">
        <h2>Countdown to the Big Day</h2>
        <div className="countdown-timer">
          {timerComponents.length ? timerComponents : <span>Time's up!</span>}
        </div>
      </section>

      <section className="details-section">
        <h2>Information kring VÄRLDENS BÄSTA BRÖLLOP</h2>
        <p>
          Vi är så glada att få dela denna speciella dag med oss! Vårt bröllop kommer vara den
          <strong> [RIKTIGT BRA DATUM]</strong> at <strong>[NÅGOT RIKTIGT FINT STÄLLE]</strong>.
        </p>
        <p>
          Utforska gärna hemsidan för att få reda på mer information, läsa lite om oss och OSA inför bröllopet.
        </p>
        <Link to="/rsvp">
          <button className="rsvp-button">RSVP Here</button>
        </Link>
      </section>

      <footer className="home-footer">
        <p>&copy; Välkomna !! </p>
      </footer>
    </div>
  );
}

export default Home;
