import React, { useEffect } from 'react';
import './Home.css';
import { Link } from 'react-router-dom';
import $ from 'jquery'; // Import jQuery
import moment from 'moment-timezone'; // Import moment-timezone
import './clock.js'; // Import your clock.js file

function Home() {

  useEffect(() => {
    // The code inside this block will run once the component mounts
    $(document).ready(function() {
      let clock;

      let currentDate = new Date();
      let targetDate = moment.tz("2023-10-29 12:00", "Asia/Kolkata");

      let diff = targetDate / 1000 - currentDate.getTime() / 1000;

      if (diff <= 0) {
        clock = $(".clock").FlipClock(0, {
          clockFace: "DailyCounter",
          countdown: true,
          autostart: false
        });
        console.log("Date has already passed!")

      } else {
        clock = $(".clock").FlipClock(diff, {
          clockFace: "DailyCounter",
          countdown: true,
          callbacks: {
            stop: function() {
              console.log("Timer has ended!")
            }
          }
        });

        setTimeout(function() {
          checktime();
        }, 1000);

        function checktime() {
          const t = clock.getTime();
          if (t <= 0) {
            clock.setTime(0);
          }
          setTimeout(function() {
            checktime();
          }, 1000);
        }
      }
    });
  }, []);

  return (
    <div className="home-container">
      <header className="hero-section">
        <div className="hero-overlay">
          <h1>Välkommen till Arthur & Amandas bröllop!</h1>
          <p>Fira med oss på DEN BÄSTA DAGEN!</p>
        </div>
      </header>

      <section className="details-section">
        <h2>Information kring VÄRLDENS BÄSTA BRÖLLOP</h2>
        <p>
          Vi är så glada att få dela denna speciella dag med oss! Vårt bröllop kommer vara den
          <strong> [RIKTIGT BRA DATUM]</strong> at <strong>[NÅGOT RIKTIGT FINT STÄLLE]</strong>.
        </p>
        <p>
          Utforska gärna hemsidan för att få reda på mer information, läsa lite om oss och OSA inför bröllopet.
        </p>
        <Link to="/rsvp">
          <button className="rsvp-button">RSVP Here</button>
        </Link>
      </section>

      <section className="clock-section">
        <h2>Countdown to the Big Day</h2>
        <div className="clock"></div> {/* This is where the clock will appear */}
      </section>

      <section className="image-gallery">
        <h2>Our Journey</h2>
        <div className="gallery">
          <img src="amandaarthur.jpg" alt="Couple" />
          <img src="image2.jpg" alt="Engagement" />
          <img src="image3.jpg" alt="Celebration" />
        </div>
      </section>

      <footer className="home-footer">
        <p>&copy; 2024 Arthur Öhman & Amanda Carp. All rights reserved.</p>
      </footer>
    </div>
  );
}

export default Home;
